import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-create-onomatopia',
  templateUrl: './create-onomatopia.component.html',
  styleUrls: ['./create-onomatopia.component.css'],
})
export class CreateOnomatopiaComponent {
  newOnomatopoeia: string ="";

  createOnomatopia(){
    this.newOnomatopoeia = inputOnomatopoeia
  }

  @Output()
  receivenOnomatopoeiaParent: EventEmitter<string> = new EventEmitter();
  sendOnomatopiaToParent():void {
    this.receivenOnomatopoeiaParent.emit(this.newOnomatopoeia)
  }
}
<form (ngSubmit)="sendOnomatopiaToParent()">
  <h4>Créer une Onomatopé</h4>
  <label for="inputOnomatopoeia">nouvelle Onomatopée :</label>
  <input
    id="inputOnomatopoeia"
    type="text"
    name="inputOnomatopoeia"
    [(ngModel)]="newOnomatopoeia"
  />
</form>
<button (click)="sendOnomatopiaToParent()"></button>
<app-root>(cookies)="createOnomatopia($event)"</app-root>
